mapper.insert:
INSERT INTO TBL_BOARD
( IDX
  , TITLE
  , CONTENTS
  , COUNT
  , WRITER
  , INDATE )
VALUES ( #{idx, jdbcType=VARCHAR}
  , #{title, jdbcType=VARCHAR}
  , #{contents, jdbcType=VARCHAR}
  , #{count, jdbcType=VARCHAR}
  , #{writer, jdbcType=VARCHAR}
  , SYSDATE() )

mapper.update:
UPDATE TBL_BOARD
SET TITLE=#{id}
, CONTENTS=#{title, jdbcType=VARCHAR}
, DESCRIPTION=#{contents, jdbcType=VARCHAR}
  WHERE IDX=#{idx}

mapper.select:
SELECT USER_NAME
FROM TBL_USER
WHERE 1=1
AND USER_ID = #{userId}
AND PASSWORD = #{password}
